import { Component, ViewChild } from '@angular/core';
import { BreakpointObserver } from '@angular/cdk/layout';
import { MatSidenav } from '@angular/material/sidenav';
import { delay } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  @ViewChild(MatSidenav)
  sidenav!: MatSidenav;

  constructor(private observer: BreakpointObserver) {}

  ngAfterViewInit() {
    this.observer
      .observe(['(max-width: 800px)'])
      .pipe(delay(1))
      .subscribe((res) => {
        if (res.matches) {
          this.sidenav.mode = 'over';
          this.sidenav.close();
        } else {
          this.sidenav.mode = 'side';
          this.sidenav.open();
        }
      });
  }
}
<mat-toolbar> 
  <h1>< Ramon Trindade /></h1>
  <button
    mat-icon-button
    *ngIf="sidenav.mode === 'over'"
    (click)="sidenav.toggle()"
  >
    <mat-icon class="menu-color" *ngIf="!sidenav.opened">menu</mat-icon>
    <mat-icon class="menu-color" *ngIf="sidenav.opened">close</mat-icon>
  </button>

</mat-toolbar>

<mat-sidenav-container>
  <mat-sidenav #sidenav="matSidenav">
    <img
      src="../assets/profile.jpg"
      alt="profile foto"
      class="avatar mat-elevation-z8"
      width="150px"
      height="160px"
    />
    <h4 class="name">Ramon Trindade</h4>
    <p class="designation">Desenvolvedor Front-End</p>

    <mat-divider></mat-divider>

    <button mat-button class="menu-button">
      <mat-icon>home</mat-icon>
      <span>Inicio</span>
    </button>
    <button mat-button class="menu-button">
      <mat-icon>construction</mat-icon>
      <span>Projetos</span>
    </button>
    <button mat-button class="menu-button">
      <mat-icon>dataset_linked</mat-icon>
      <span>Tech Skills</span>
    </button>
    <button mat-button class="menu-button">
      <mat-icon>person</mat-icon>
      <span>Contato</span>
    </button>
    <mat-divider></mat-divider>
    <button mat-button class="menu-button">
      <mat-icon>info</mat-icon>
      <span>Sobre mim</span>
    </button>
  </mat-sidenav>
  <mat-sidenav-content>
    <div class="content mat-elevation-z4">Main Content</div>
  </mat-sidenav-content>
</mat-sidenav-container>
